<?phpuse Phroute\Phroute\RouteCollector;use Phroute\Phroute\RouteParser;require_once 'vendor/autoload.php';$parser=new RouteParser();$router=new RouteCollector();$router->controller('/',\App\Controller\HomeController::class);$router->get('/about',function (){    echo "about";});$dispatcher = new Phroute\Phroute\Dispatcher($router->getData());try {    $response = $dispatcher->dispatch($_SERVER['REQUEST_METHOD'], parse_url($_SERVER['REQUEST_URI'], PHP_URL_PATH));} catch (Exception $e){    echo $e->getMessage();}